/*Data for the table `tb_courseintrod` */

insert  into `tb_courseintrod`(`id`,`courseIntrod`,`introdCont`,`courseFeatures`,`featuresCont`,`courseHistory`,`historyCont`,`teach`,`teachCont`,`created`) values (4,'课程介绍','<p><span style=\"font-family: Arial, Helvetica, sans-serif; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp;《信号与系统》是电子信息类专业本科生继“电路分析基础”课程之后必修的重要主干课程。该课程主要研究确知信号的特性，线性时不变系统的特性，信号通过线性时不变系统的基本分析方法，以及信号与系统分析方法在某些重要工程领域的应用。通过本课程的学习，使学生掌握信号分析与线性系统分析的基本理论及分析方法，能对工程中应用的简单系统建立数学模型，并对数学模型求解。为适应信息科学与技术的飞速发展，及在相关专业领域的深入学习打下坚实的基础。同时，通过习题和讨论，学生应在分析问题与解决问题的能力及实践技能方面有所提高。&nbsp;</span></p>',NULL,NULL,NULL,NULL,NULL,NULL,'2018-05-21'),(10,'课程负责人','<p>&nbsp; &nbsp; &nbsp; &nbsp;课程负责人课程负责人课程负责人课程负责人课程负责人</p><p>课程负责人课程负责人课程负责人课程负责人</p><p>课程负责人课程负责人课程负责人课程负责人</p><p>课程负责人课程负责人课程负责人</p><p>课程负责人课程负责人课程负责人</p>',NULL,NULL,NULL,NULL,NULL,NULL,'2018-05-22'),(11,NULL,NULL,NULL,NULL,'课程历史沿革','<p style=\"text-align:center;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号与系统课程历史沿革</span></p><p style=\"text-indent:24px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">《信号与系统》作为信号分析与系统分析的基础理论，具有重要的实际应用意义，现实中信号与系统问题无处不在，它的基本概念、基本分析方法已经渗透到所有现代自然科学和社会科学领域。本课程在整个教学体系中起到承上启下的作用，它不仅联系电路、复变函数知识，更加关系到通信原理、数字信号处理、自动控制原理等专业课的学习，同时该课程是大多工科类专业特别是电子信息类专业本科生的一门重要的专业基础课程，也是绝大多数高校“通信与信息系统”“信号与信息处理”等专业硕士研究生入学考试的必考科目。因此该课程对本科专业人才的培养至关重要。</span></p><p style=\"text-indent:24px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2008</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年我院招收第一届电子信息科学与技术专业学生，就在第五学期开设了信号与系统这门课程，教学重点在各种变换性质的推导，教学学时：理论教学72学时。由于我院学生生源数学推导能力弱，往往只注意运算结果的对错，不去归纳总结解决问题的方法，对运算结果分析能力不足，因此教学效果不够理想。随着信息技术不断革新，社会需要的技能型人才不断革新，高校的人才培养模式也亟需转型。为了顺应现代数字技术的发展趋势，地方高校积极向应用型转变，而传统理论较强的课程，存在学时多、课程内容重复、偏重公式计算等教学问题。为此，我院《信号与系统》课程进行了如下改革。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">一、调整课程体系，重新认识课程结构</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">在传统的教学中，教师主要按照课本讲授理论知识，计算公式繁多。对于《信号与系统》课程，教学内容概括为：一个前提，两种积分，三大变换。所以只要抓住这一主旨内容就可以把握 《信号与系统》课程的整体框架。其中一个前提是本门课程中所研究的系统均为线性时不变（LTI-Linear Time Invari-ant）系统。两种积分为连续信号的卷积积分和离散信号的卷积和，这两种卷积运算是用于求系统零状态响应的一种非常有效的方法。三大变换即傅立叶变换、拉普拉斯变换和Z变换。三大变换是本门课程的重点及难点，抓住以三大变换为主线精讲，一个前提作为基石，两种积分作为辅助，理论联系实际，课程讲授脉络才能清楚。这样整个理论课学时72学时，调整到第四学期学习，这样可以增加实际应用教学，让学生在实际中学习《信号与系统》，促使学生深入掌握知识，为构建完整的专业知识体系奠定基础。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">二、改革教学方法，发展启发式教学</span></p><p style=\"text-indent:24px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">在教学中，应尽可能弱化数学推导，弱化公式推导并不是不推导而给出结论，而是在保证学生知道知识来龙去脉的前提下，不纠缠于数学演算细节。形象地利用图，让学生从视觉感官上能看到关系，比起直接数学推导公式理解起来更容易。例如在讲解正弦波叠加成方波时利用各次谐波的叠加图对比最终得到结果。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">三、改善授课手段，提高学习兴趣</span></p><p style=\"text-indent:24px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">传统的课堂教学手段采用多媒体与板书相结合。一般定理的推导和例题的讲解利用传统板书的方式教学。在使用多媒体教学过程中，讲授阶段要充分发挥教师的主导作用，要使得课件跟着教师走，不是教师被动死板地念课件。合理设计课件，增加趣味性，使学生有兴趣学习下去。例如在讲述傅立叶变换频移特性时，由于频移特性应用极为广泛，只照课本叙述的话，学生印象肯定不深刻，在授课中加入现实生活中广播的实例，增加学生的兴趣。</span></p><p style=\"line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">四、增加实验课时，实现理论与实际相结合</span></p><p style=\"line-height:150%\"><span style=\"font-size: 16px;line-height:150%;font-family:宋体\">&nbsp;&nbsp; &nbsp;</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">《信号与系统》是偏理论的课程，凭借MATLAB独特的优势，用来完成纯数值计算、信号、系统的建模和仿真，可以把抽象理论可视化，不但提高学生学习兴趣，还培养学生自主获得知识和单独解决学习、生活中遇到问题的能力。因此我院分别在第三学期开设了《虚拟仿真实训》和第五学期开设了《信号处理应用实训》，为信号处理类课程的理论学习作为了依托。</span></p><p><br/></p>',NULL,NULL,'2018-05-25'),(12,NULL,NULL,'课程特色','<p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">《信号与系统》课程采用“解题式突破”——问题导向的课程建设策略。</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">美国课程专家塔巴</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">(H.Taba)</span><span style=\"font-size:16px;font-family:宋体\">认为，行动研究视为学校课程变革不可或缺的工具。教师应通过研究解决他们在课程改革中所遇到的问题，并将解决问题的思路与方法付诸实践。因此，课程建设本质上就是直面现实问题、以解题式思维突破课程问题的变革过程，是问题导向的课程理论与实际情境的不断循环对话过程。</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">《信号与系统》的课程改革借鉴塔巴</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">(H.Taba)</span><span style=\"font-size:16px;font-family:宋体\">的课程改革理论，建设内涵及方法由问题倒逼而产生，又在不断解决问题中而深化。发现问题，寻找对策，不断解决问题，突破问题对我们的限制，这就是《信号与系统》课程建设的总体思路和策略。</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">塔巴</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">(H.Taba)</span><span style=\"font-size:16px;font-family:宋体\">为解决课程变革实践问题的行动研究提供了一套过程模式，因此《信号与系统》课程建设参照其过程模式，将“解题式突破”的实际操作分解为以下七个步骤：</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">①</span><span style=\"font-size: 16px;font-family:宋体\">明确问题：主要通过两种途径：一是通过任课教师总结反思前期课程教学过程中存在的主要问题；二是向学生发放调查问卷并利用座谈会的形式收集学生对课程教学意见的反馈；</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">②</span><span style=\"font-size: 16px;font-family:宋体\">分析问题：针对上一环节中找出的问题进行梳理和分类，从教学内容、教学安排、教学方法、教学环境等角度进行切入和归类；</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">③</span><span style=\"font-size: 16px;font-family:宋体\">确定解决问题的思路和方法；</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">④</span><span style=\"font-size: 16px;font-family:宋体\">收集与整理资料；</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">⑤</span><span style=\"font-size: 16px;font-family:宋体\">制定行动方案；</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">⑥</span><span style=\"font-size: 16px;font-family:宋体\">实施行动。</span></p><p style=\"text-indent:32px;line-height:29px\"><span style=\"font-size:16px;font-family:宋体\">⑦</span><span style=\"font-size: 16px;font-family:宋体\">评价行动结果。</span></p><p><br/></p>',NULL,NULL,NULL,NULL,'2018-06-06');

