insert  into `tb_news`(`id`,`newsName`,`newsTitle`,`newsCategory`,`news`,`newsCont`,`status`,`created`,`updated`) values (3,'读书','世界读书日，商院学子朗诵经典 ',NULL,NULL,'<p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; &nbsp;在世界读书日到来之际，我院图书馆举办了以“阅读趁年华，书香溢商院”为主题的系列读书活动。4月17日下午，活动之一的中华经典诗文朗诵会决赛在图书馆报告厅举行。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419085552930976.JPG\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp;本次朗诵会，共有各院系报名的400余支队伍参赛，最终19支队伍入围决赛。学生们在诵读《将进酒》《我的祖国》等经典的同时，还将表演融合其中，内容精彩，气质独特。其中，留学生组合成为活动亮点，来自俄罗斯、日本、捷克等国家的留学生踊跃参与，他们身着民国服饰，带着对中华经典诗文的向往和追求，与中国学生一起朗诵《追梦，我们在路上》，汉语相对标准，富有感情，赢得在场师生的一致好评，其中的一位俄罗斯留学生说：“我学习汉语八个月了，很喜欢中国传统文化，我将继续留在中国，认真学习中国文化。”</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419085619635018.JPG\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp;据了解，本次读书活动将持续近一个月，还将有“智慧平台”体验、征文比赛、图书真人秀等创意活动，与往届活动相比更能吸引师生回归深度阅读，让书香溢满校园。“全体学生必须以高度的紧迫感和责任担当意识强化学习，让阅读成为推动学习的新引擎、新动力。”图书馆馆长郭启智希望学生养成良好的阅读习惯，通过阅读掌握知识、提升能力。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419085438769790.jpg\" style=\"width: 600px; height: 437px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419110319402788.jpg\" style=\"width: 600px; height: 598px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419085454551801.jpg\" style=\"width: 600px; height: 449px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180419093742261576.jpg\" style=\"width: 600px; height: 434px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;</p><p><br/></p>',NULL,'2018-04-24','2018-04-24'),(5,'院校建设','我院与达内时代科技集团共建实验室通过验收',NULL,NULL,'<p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;4月17日上午，学院教务处组织专家对我院与达内时代科技集团有限公司共建的大数据技术实验室和移动互联技术实验室的建设工作进行了验收，共建实验室位于我院产教融合校企合作实训大楼一层，使用面积320平方米，投资150万元，主要服务于信息学院计算机科学与技术、网络工程等5个专业学生的实验实训、专业实习、课程设计、毕业设计、创新创业教育及技术研发等工作。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp;验收专家组一行在副院长杨继平的带领下，通过听取汇报、实地考察、操作演示等形式，围绕实验室建设的目标、任务，实验实训教学环境、仪器设备性能、开设的课程和开设的实验实训项目进行了认真的考察验收。经专家组讨论，认为校企共建实验室建设成效明显，达到预期目标，一致同意通过验收。</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp;杨院长在总结讲话中，首先对信息学院和达内时代科技集团有限公司相关人员在共建实验室建设中的辛勤付出表示衷心感谢；其次对共建实验室功能发挥提出了新的要求，此次我院校企合作共建实验室的顺利验收，是万里长征的第一步，今后双方要进一步深化产教融合校企合作、协同育人，在实验实训教学、项目开发、教材编写、教师培训等方面取得更大成绩，不断提高应用型人才培养质量。</p><p><br/></p>',NULL,'2018-04-24','2018-04-24'),(6,'我院举办第十八届运动会','我院举办第十八届运动会',NULL,NULL,'<p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: Simsun;\">&nbsp; 4月26日，我院第十八届运动会在田径场隆重开幕。党委书记吴建社在开幕式上致辞，学院领导、各职能部门、院系负责人出席开幕式。开幕式由学院副院长杨继平主持。</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428151822862351.jpg\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">党委书记吴建社致辞</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: Simsun;\">　　党委书记吴建社在致辞中对本次运动会开幕表示热烈祝贺：一年一度的运动会，是展示我院师生员工运动之美、力量之美的盛会，也是展示我们深入推进学院改革的决心、信心和毅力的窗口。我相信，只要全院师生凝心聚力，众志成城，认真做好各项工作，学院的前进步伐就会更加扎实，学院的发展道路就会更加宽阔！希望全体运动员在赛场上奋勇拼搏超越、磨炼坚强意志、诠释体育精神，展示出当代大学生的良好素质和时代风采；希望全体裁判员和工作人员恪尽职守，为人师表，引导学生培育正直、诚实、奉献、拼搏、百折不挠、自尊自信的品格；希望各有关部门精心组织，热心服务，相互学习，共同提高，努力营造隆重热烈、团结进取、充满友谊与活力的浓厚育人氛围。</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428152121246562.jpg\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">学院领导为获奖院系颁奖</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\">　　<span style=\"font-family: simsun;\">伴随着《运动员进行曲》，由学生国旗护卫队带队、校旗方队、校徽方队及教练员各院系运动员方队依次入场。盛大的开幕式文艺表演掀起开幕式的高潮，拉开了本次运动会的帷幕。本届运动会共设立24个竞赛项目、5个团体竞技项目，参赛运动员达977人。</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154142632919.jpg\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154157484054.jpg\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154215926160.JPG\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154226549270.JPG\" style=\"width: 600px; height: 400px;\"/></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">学生国旗护卫队、学生护卫队护旗方队及各院系运动员方队入场</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255);\"><span style=\"font-family: simsun;\">　　</span><span style=\"font-family: simsun;\">经过三天的激烈竞争，会计学院团体总分蝉联第一。</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: simsun;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154241931365.JPG\" style=\"width: 600px; height: 400px;\"/>.</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: simsun;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154254708493.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: simsun;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154320353511.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: simsun;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154328442636.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">精彩的开幕式表演</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154342328765.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">团结协作的趣味赛</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154352267813.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">教师代表队也热情参与其中</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154401954904.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\"><img alt=\"\" src=\"http://www.sxdxswxy.com/images/content/2018-04/20180428154413527047.jpg\" style=\"width: 600px; height: 400px;\"/></span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\"><span style=\"font-family: 黑体;\">竞争激烈的田径赛</span></p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: center;\">&nbsp;</p><p style=\"font-family: &quot;Microsoft YaHei&quot;; white-space: normal; background-color: rgb(255, 255, 255); text-align: right;\"><span style=\"font-family: simsun;\">（学院办公室&nbsp;</span><span style=\"font-family: 黑体;\">摄</span><span style=\"font-family: simsun;\">）</span></p><p><br/></p>',NULL,'2018-05-09','2018-05-09');

/*Table structure for table `tb_picupload` */

DROP TABLE IF EXISTS `tb_picupload`;

CREATE TABLE `tb_picupload` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `picName` varchar(100) DEFAULT NULL,
  `picTitle` varchar(50) DEFAULT NULL,
  `picCategory` varchar(50) DEFAULT NULL,
  `couresLeader` varchar(2000) DEFAULT NULL,
  `courseIntr` varchar(2000) DEFAULT NULL,
  `courseCont` varchar(2000) DEFAULT NULL,
  `picUrl` varchar(100) DEFAULT NULL,
  `picStatus` int(10) DEFAULT NULL,
  `created` date DEFAULT NULL,
  `updated` date DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

