/*Data for the table `tb_teachcondition` */

insert  into `tb_teachcondition`(`id`,`textBooks`,`textCont`,`practiceCond`,`condCont`,`netCond`,`netCont`,`created`) values (1,'教材及教学内容','<p style=\"text-align:center;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教材及教学内容</span></p><p class=\"MsoListParagraph\" style=\"margin-left:60px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">一、</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">使用教材情况</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px;text-indent:0;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">、《信号与系统》郑君里主编</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px;text-indent:16px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">偏重于基本概念、基本方法、理论性强、强调数学的推导。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px;text-indent:0;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">、《信号与系统》李静 翟亚芳主编</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px;text-indent:16px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">弱化数学推导、不纠缠于数学演算细节、强化应用能力，迁移拓展。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:60px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">二、</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学内容</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">第1章 信号与系统导论</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">基本要求：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">掌握信号、系统的概念，以及它们之间的关系；了解信号的函数表示与图形表示以及信号的运算；掌握阶跃信号、冲激信号，及其性质、相互关系；了解系统的模型和分类，重点掌握线性时不变系统的性质。 </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要内容：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">历史的回顾</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">应用领域</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号的概念</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的概念</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">常用的基本信号</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.6</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号的简单处理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">1.7</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">单位冲激函数</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所需学时：10</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">重点难点：1.信号和系统的概念 2.常见信号的表示 3.阶跃信号和冲激信号 4.线性时不变系统的性质 </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学方式：讲授</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">第2章 连续系统的时域分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">基本要求：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">会建立描述系统激励与响应关系的微分方程；了解系统的特征方程、特征根的意义。深刻理解系统全响应的三种分解方式：零输入响应与零状态响应；自由响应与强迫响应；瞬态响应与稳态响应。深刻理解系统单位冲激响应和阶跃响应。牢固掌握卷积积分的运算规律、主要性质（微分、积分、时移、卷积性质），能够进行卷积运算。能够应用卷积积分方法求线性时不系统的零状态响应。 </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要内容：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">线性时不变系统描述及其响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.1.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的微分方程</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.1.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">零输入响应与零状态响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">冲激响应与阶跃响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.2.1 </span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">冲激响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.2.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">阶跃响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.2.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">利用转移算子求冲激响应</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">卷积及其应用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.3.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">卷积的概念与性质</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.3.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的卷积分析法</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.3.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">卷积的计算:图形扫描法</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">2.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">特征函数及其应用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所需学时：16</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">重点难点：1.系统全响应，包含三种分解方式 2.系统单位冲激响应和阶跃响应 3.卷积积分的计算方法 </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学方式：讲授</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">第3章 连续系统的时域分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">基本要求：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">了解周期信号的级数表示。理解周期信号频谱的特点，非周期信号的频谱密度，会画频谱图。 牢记傅立叶变换的定义及典型信号的傅立叶变换，熟练掌握傅立叶变换性质。深刻理解和掌握抽样定理及在实际工程问题中的应用。了解傅里叶变换在通信系统中的应用，会通过系统函数求系统的响应；充分理解系统无失真传输的条件；理解通信系统中常用的调制解调原理；了解通系统中常用的频分复用和时分复用方式。</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要内容：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号的分解与合成</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.1.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号的三角级数表示</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.1.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号的复指数级数表示</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号的频谱</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.2.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号频谱的特点</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.2.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">双边频谱与信号的带宽</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">非周期信号的频谱</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.3.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">傅里叶变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.3.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">常用非周期信号的频谱</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.3.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">帕塞瓦尔定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">傅里叶变换的性质与应用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">线性性质</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">脉冲展缩与频带变化</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号的延时与相位移动</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号的调制与频谱搬移</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">时一频对称性</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.6</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">卷积定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.7</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">时域微分特性</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.4.8</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">时域积分特性</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">周期信号的傅里叶变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.6</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的频域分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.6.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统函数与无失真传输</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.6.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号通过理想滤波器</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.7</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">取样定理及其应用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.7.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">取样信号</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.7.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">取样定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.8</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">频域分析用于通信系统</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.8.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">信号的调制与解调</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.8.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">正弦调幅与频分复用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">3.8.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">脉冲调幅与时分复用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所需学时：18</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">重点难点：1.周期信号的傅里叶级数&nbsp; 2.非周期信号的傅里叶变换 3.冲激函数和阶跃函数的傅里叶变换 4.傅里叶变换的性质 5.抽样定理6.利用系统函数求系统响应7.系统无失真传输条件 8取样定理&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学方式：讲授</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">第4章 连续系统的复频域分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">基本要求：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">深刻理解拉普拉斯变换的定义式、收敛域及基本性质；能够根据拉普拉斯的定义式及基本性质求常用信号的拉普拉斯变换。掌握部分分式展开法求解拉普拉斯逆变换。掌握电路的S 域表示形式；能根据时域电路模型正确的画出 S 域电路模型。熟练掌握连续系统的复频域分析法，会求解全响应，零输入响应，零状态响应，以及冲激响应与阶跃响应。</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要内容：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">拉普拉斯变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.1.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">从傅里叶变换到拉普拉斯变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.1.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">常用信号的拉普拉斯变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">拉普拉斯变换的主要性质</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">线性性质</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">延时性质</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">微分定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">积分定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.5</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">卷积定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.2.6</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">初值与终值定理</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">拉普拉斯反变换</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.4</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的s域分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.4.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">微分方程的拉普拉斯变换解法</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">4.4.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">电路的s域模型及应用</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所需学时：16</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">重点难点：1.拉普拉斯变换的定义及收敛域2.拉普拉斯变换的性质 3.拉普拉斯逆变换 4.S 域电路模型 5.连续时间系统的复频域分析 6.拉普拉斯变换与傅里叶变换的关系&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学方式：讲授</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">第5章 系统函数与零、极点分析</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">基本要求：</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">深刻理解系统函数 H(S) 的概念；了解 H(S) 的零、极点分布与时域特性的关系，掌握系统的稳定性判据。</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要内容</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统函数与系统模拟</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.1.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">再论系统函数</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.1.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统的方框图表示与模拟</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统函数的零、极点</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.2.1 H(s)</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的零、极点</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.2.2 H(s)</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的零、极点分布与时域特性的关系</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.2.3 H(s)</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的零、极点分布与频域特性的关系</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.3</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">线性系统的稳定性</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.3.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统稳定的概念</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.3.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">稳定性判据</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.4 s</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">域分析应用于控制系统</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.4.1</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">开环与闭环控制</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">5.4.2</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统阶跃响应的动态指标</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所需学时：12</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">重点难点：1由系统函数零极点分布决定系统时域和频域特性2系统稳定性的定义及其判断方法</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">教学方式：讲授</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p style=\"margin-left:28px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">&nbsp;</span></p><p><br/></p>',NULL,NULL,NULL,NULL,'2018-05-26'),(5,NULL,NULL,NULL,NULL,'网络教学条件','<p style=\"margin-bottom: 21px; text-align: left; line-height: 150%;\"><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">&nbsp; &nbsp;随着社会生活节奏的加快和生活方式的多元化，作为年轻一代的学生，他们越来越喜欢接受具有简单、美观、快捷、有趣的学习或生活方式，比如“微电影、微博、微信、微小说”等。而随着新通信技术和人工智能的发展、新网络教育模式的大量涌现、商业产业互联网</span><span style=\"text-indent: 32px; line-height: 150%; font-family: &quot;Times New Roman&quot;, serif;\">+</span><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">，这些都在推动服务产品向着“小、精、美”方向发展，这就是现实社会中出现的微生活。伴随手机、</span><span style=\"text-indent: 32px; line-height: 150%; font-family: &quot;Times New Roman&quot;, serif;\">PAD</span><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">、智能手表等各种智能电子设备大量走入课堂，学生对传统的教学模式也逐渐开始缺乏兴趣，转而追求个性化的学习方式。</span><br/></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">因此课程组反复试验、尝试，寻找与《信号与系统》课程性质和教学需求契合度高的教学工具，最后在课堂教学中引入了学生感兴趣的基于移动（手机）端教育学习软件—雨课堂（</span><a href=\"https://www.yuketang.cn/web?index\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">https://www.yuketang.cn/web?index</span></a><span style=\"font-size:16px;line-height:150%;font-family:宋体\">），如图</span><span style=\"font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">9</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所示。</span></p><p style=\"text-align:center;text-indent:32px;line-height:150%\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"http://p40nr3qwg.bkt.clouddn.com/5.png\"/>&nbsp;</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: center;\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">图</span><span style=\";line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">9&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">“雨课堂”网页版界面图</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">雨课堂旨在“让教与学释放更多能量”，创新引领课堂革命，是推动混合式教学，形成性评价，多通道互动，得到教育部部长点赞的教学利器。其特点是“小插件，大数据”：将教学工具巧妙融入</span><span style=\"font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">PowerPoint</span><span style=\"font-size:16px;line-height: 150%;font-family:宋体\">与微信全景式采集数据，驱动教学探微。如图</span><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">10</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所示。</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">雨课堂让学生、教师和管理者知道教室里真正发生了什么。它有助于实现数据驱动的教学，帮助教师在提升教学艺术的同时，实现科学教学和教学科学。</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">课程组在教学过程中充分利用现代化教育资源，采用智能化教学的方式，提高学生的学习兴趣、培养学生的动手和创新能力。</span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: center;\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\"><img src=\"http://p40nr3qwg.bkt.clouddn.com/6.png\"/></span></p><p></p><p style=\"text-align:center;text-indent:28px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">图</span><span style=\";line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">10&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">“雨课堂”特点</span></p><p style=\"line-height: 150%;\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">利用雨课堂课前推送功能实现课前预习的环节、利用实时答题、多屏互动、答疑弹幕、投屏等功能实现课堂互动、讨论的关节，利用扫码签到、学生数据分析等功能分析课堂教学效果、收集课堂反馈，进而不断改进课堂教学效果。使得教师与学生通过互联网实现教学一体化的全新教育模式。</span></p>','2018-06-07');

/*Table structure for table `tb_teachcontent` */

DROP TABLE IF EXISTS `tb_teachcontent`;

CREATE TABLE `tb_teachcontent` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `theoryTeach` varchar(50) DEFAULT NULL,
  `theoryCont` text,
  `practiceTeach` varchar(50) DEFAULT NULL,
  `practiceCont` text,
  `teachOrg` varchar(50) DEFAULT NULL,
  `orgCont` text,
  `created` date DEFAULT NULL,
  PRIMARY KEY (`id`)
