) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `tb_teacher` */

insert  into `tb_teacher`(`id`,`teachNum`,`relname`,`password`,`password2`,`uppassword`,`email`,`createDate`) values (1,'100011','张老师','123456','123456',NULL,'zhangzkx@126.com','2018-04-15'),(3,'10004','高老师','123456','123456',NULL,'gaolaoshi@126.com','2018-04-15'),(4,'10005','王华','123456','123456',NULL,'wanghua@126.com','2018-04-15'),(5,'10006','王老师','123456','123456',NULL,'wanglaoshi@163.com','2018-04-23'),(6,'100022','王哈哈','123456','123456',NULL,'wanghaha@163.com','2018-05-01');

/*Table structure for table `tb_teachmethod` */

DROP TABLE IF EXISTS `tb_teachmethod`;

CREATE TABLE `tb_teachmethod` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `teachMethod` varchar(50) DEFAULT NULL,
  `methodCont` text,
  `created` date DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

/*Data for the table `tb_teachmethod` */

insert  into `tb_teachmethod`(`id`,`teachMethod`,`methodCont`,`created`) values (5,'教学方法','<p style=\"margin-bottom: 21px; text-align: left; line-height: 150%;\"><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">&nbsp; &nbsp;随着社会生活节奏的加快和生活方式的多元化，作为年轻一代的学生，他们越来越喜欢接受具有简单、美观、快捷、有趣的学习或生活方式，比如“微电影、微博、微信、微小说”等。而随着新通信技术和人工智能的发展、新网络教育模式的大量涌现、商业产业互联网</span><span style=\"text-indent: 32px; line-height: 150%; font-family: &quot;Times New Roman&quot;, serif;\">+</span><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">，这些都在推动服务产品向着“小、精、美”方向发展，这就是现实社会中出现的微生活。伴随手机、</span><span style=\"text-indent: 32px; line-height: 150%; font-family: &quot;Times New Roman&quot;, serif;\">PAD</span><span style=\"text-indent: 32px; line-height: 150%; font-family: 宋体;\">、智能手表等各种智能电子设备大量走入课堂，学生对传统的教学模式也逐渐开始缺乏兴趣，转而追求个性化的学习方式。</span><br/></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">因此课程组反复试验、尝试，寻找与《信号与系统》课程性质和教学需求契合度高的教学工具，最后在课堂教学中引入了学生感兴趣的基于移动（手机）端教育学习软件—雨课堂（</span><a href=\"https://www.yuketang.cn/web?index\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">https://www.yuketang.cn/web?index</span></a><span style=\"font-size:16px;line-height:150%;font-family:宋体\">），如图</span><span style=\"font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">9</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所示。</span></p><p style=\"text-align:center;text-indent:32px;line-height:150%\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"http://p40nr3qwg.bkt.clouddn.com/5.png\"/></span></p><p style=\"text-indent: 32px; line-height: 150%; text-align: center;\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">图</span><span style=\";line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">9&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">“雨课堂”网页版界面图</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">雨课堂旨在“让教与学释放更多能量”，创新引领课堂革命，是推动混合式教学，形成性评价，多通道互动，得到教育部部长点赞的教学利器。其特点是“小插件，大数据”：将教学工具巧妙融入</span><span style=\"font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">PowerPoint</span><span style=\"font-size:16px;line-height: 150%;font-family:宋体\">与微信全景式采集数据，驱动教学探微。如图</span><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">10</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">所示。</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">雨课堂让学生、教师和管理者知道教室里真正发生了什么。它有助于实现数据驱动的教学，帮助教师在提升教学艺术的同时，实现科学教学和教学科学。</span></p><p style=\"text-indent:32px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">课程组在教学过程中充分利用现代化教育资源，采用智能化教学的方式，提高学生的学习兴趣、培养学生的动手和创新能力。</span></p><p style=\"text-align: center;\"><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"http://p40nr3qwg.bkt.clouddn.com/6.png\"/></span></p><p style=\"text-align:center;text-indent:28px;line-height:150%\"><span style=\";line-height:150%;font-family:宋体\">图</span><span style=\";line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">10&nbsp; </span><span style=\";line-height:150%;font-family:宋体\">“雨课堂”特点</span></p><p style=\"line-height: 150%;\"><span style=\"font-size: 16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">利用雨课堂课前推送功能实现课前预习的环节、利用实时答题、多屏互动、答疑弹幕、投屏等功能实现课堂互动、讨论的关节，利用扫码签到、学生数据分析等功能分析课堂教学效果、收集课堂反馈，进而不断改进课堂教学效果。使得教师与学生通过互联网实现教学一体化的全新教育模式。</span></p>','2018-06-07');

/*Table structure for table `tb_teachteam` */

DROP TABLE IF EXISTS `tb_teachteam`;

CREATE TABLE `tb_teachteam` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `courseLeader` varchar(50) DEFAULT NULL,
  `leaderCont` text,
  `courseTeach` varchar(50) DEFAULT NULL,
  `TeachCont` text,
  `teachTeam` varchar(50) DEFAULT NULL,
  `teamCont` text,
  `created` date DEFAULT NULL,
  PRIMARY KEY (`id`)
