/*Data for the table `tb_teachteam` */

insert  into `tb_teachteam`(`id`,`courseLeader`,`leaderCont`,`courseTeach`,`TeachCont`,`teachTeam`,`teamCont`,`created`) values (2,'课程负责人','<p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-family: Arial, Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp; &nbsp; &nbsp; &nbsp; 樊东燕，教授，硕士生导师，信息学院副院长，信息网络省级实验教学中心负责人，高级软件分析师，中国电子学会高级会员，中国计算机学会（CCF）会员，山西省高校电子技术教学研究会常务理事。主要从事信息处理技术、计算机应用技术相关学科的教学与研究工作。主持、参与山西省科技厅、山西省教育厅等省部级科研及教改项目十多项，在国内外重要学术刊物上发表论文20余篇，主编或参与出版教材6部，省级精品课程主讲教师，获得山西省高等学校优秀教学成果二等奖、三等奖各一项，获“2010年度中国商业联合会科学技术奖,全国商业科技进步奖”三等奖一项，获全国高等学校计算机课件评比二等奖、三等奖各一项。指导学生参加全国软件程序设计大赛、数学建模大赛获奖多次。</p><p style=\"margin-top: 0px; margin-bottom: 0px; padding: 0px; list-style: none; font-family: Arial, Helvetica, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;樊东燕教授多年来带领《软件工程》课程教学团队脚踏实地、坚持不懈地对课程理论教学体系和实践教学体系进行探索和实践，课程建设效果显著，《软件工程》课程先后被评为山西大学商务学院系级重点建设课程、院级优秀课程。</p><p><br/></p>',NULL,NULL,NULL,NULL,'2018-05-22'),(4,NULL,NULL,NULL,NULL,'教学组织结构','<p>&nbsp; &nbsp; &nbsp; &nbsp;教学组织结构教学组织结构教学组织结构教学组织结构</p><p>教学组织结构教学组织结构教学组织结构教学组织结构</p><p>教学组织结构教学组织结构教学组织结构教学组织结构</p><p>教学组织结构教学组织结构教学组织结构教学组织结构</p>','2018-05-22'),(5,NULL,NULL,'课程主讲教师','<table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr class=\"firstRow\"><td width=\"63\" style=\"border: 1px solid windowtext; background: rgb(149, 179, 215); padding: 0px 7px;\"><p><span style=\"font-family:宋体\">教师</span></p><p><span style=\"font-family:宋体\">姓名</span></p></td><td width=\"39\" style=\"border-top: 1px solid windowtext; border-right: 1px solid windowtext; border-bottom: 1px solid windowtext; border-image: initial; border-left: none; background: rgb(149, 179, 215); padding: 0px 7px;\"><p><span style=\"font-family:宋体\">性别</span></p></td><td width=\"29\" style=\"border-top: 1px solid windowtext; border-right: 1px solid windowtext; border-bottom: 1px solid windowtext; border-image: initial; border-lef